$(function(){const t=$("#spreadsheet").kendoSpreadsheet({toolbar:!1,sheetsbar:!1,sheets:[spreadsheet]});var n=$("#spreadsheet").data("kendoSpreadsheet").activeSheet();n.frozenRows(1);n.frozenColumns(1);$("#save").click(function(){if(!$(this).hasClass("k-state-disabled")){const n=t.data("kendoSpreadsheet").toJSON();$.ajax({type:"POST",url:"/data/UpdateDataSet",data:JSON.stringify(n),contentType:"application/json; charset=utf-8",dataType:"json",success:function(n){n===!0?alert("Data saved successfully!"):alert(n)}})}});$("#cancel").click(function(){$(this).hasClass("k-state-disabled")||location.reload(!0)})});