$(function(){var n=$("#spreadsheet").kendoSpreadsheet({toolbar:!1,sheetsbar:!1,sheets:[spreadsheet]});$("#save").click(function(){if(!$(this).hasClass("k-state-disabled")){var t=n.data("kendoSpreadsheet").toJSON();$.ajax({type:"POST",url:"/data/UpdateDataSet",data:JSON.stringify(t),contentType:"application/json; charset=utf-8",dataType:"json",success:function(n){n===!0?alert("Details saved successfully!!!"):alert(n)}})}});$("#cancel").click(function(){$(this).hasClass("k-state-disabled")||location.reload(!0)})});